query.pigql:
q = load "${App.EdgeMarts.ServiceCase.Alias}";
q = filter q by IsClosed=="true"; q = filter q by {{cell(Time_Selection.selection, 0, "Current").asDateRange("date('${Constants.ClosedDate}_Year','${Constants.ClosedDate}_Month','${Constants.ClosedDate}_Day')")}}; 
hasArticle = filter q by 'ArticleVersion' > 0;  
c = group hasArticle by '${Constants.CaseSeverityLevel}', q by '${Constants.CaseSeverityLevel}'; 
c = foreach c generate count(hasArticle)/count(q)  as 'avg_AttachedRate', hasArticle['${Constants.CaseSeverityLevel}'] as '${Constants.CaseSeverityLevel}'; c = order c by 'avg_AttachedRate' {{cell(static_1.selection, 0, "value").asString()}};
